function copy_url(type){const msg=document.getElementById(`msg`);const title=document.title;const url=location.href;const txt=(()=>{if(type===`title_url`)
return `${title}\n\n${url}`;else
return url;})();navigator.clipboard.writeText(txt).then(()=>{msg.classList.add(`toast-msg`);setTimeout(()=>{msg.classList.remove(`toast-msg`);},1500);});}
function jump_tag(e){if(e.key!=`Enter`&&e.type!=`click`)
return;const tag_val=document.getElementById(`input-tag-comple`).value.trim().replace(/\s+/,` `);if(tag_val==``)
return;const list=document.getElementById(`tags-list`);const selector=list.querySelector(`[value="${tag_val}"]`);if(!selector){const err_msg=document.getElementById(`tag-comple-error`);const wrong_tag=(()=>{if(25<=tag_val.length)
return `${tag_val.substring(0,25)}...`;else
return tag_val})();err_msg.innerHTML=`"${wrong_tag}" not found`;err_msg.classList.add(`toast-err`);setTimeout(()=>{err_msg.classList.remove(`toast-err`);},2000);return;}
location.href=`/tags/${selector.dataset.url}/`;}
function clear_value(){return document.getElementById(`input-tag-comple`).value=``;}