const title=document.title;const url=window.location.href;const msg=document.getElementById("msg");function toast_msg(){msg.classList.add("toast-msg");setTimeout(function(){msg.classList.remove("toast-msg");},2000);}
function copy_url(){navigator.clipboard.writeText(url).then(function(){toast_msg();});}
function copy_title_url(){navigator.clipboard.writeText(title+"\n\n"+url).then(function(){toast_msg();});}