var title=document.title;var url=window.location.href;function toast_msg(){var msg=document.getElementById("msg");msg.classList.add("toast-msg");setTimeout(function(){msg.classList.remove("toast-msg");},3000);}
function copy_url(){navigator.clipboard.writeText(url).then(function(){toast_msg();});}
function copy_title_url(){navigator.clipboard.writeText(title+"\n\n"+url).then(function(){toast_msg();});}