function toast_msg(){msg.classList.add("toast-msg");setTimeout(function(){msg.classList.remove("toast-msg");},1500);}
function copy_url(type){const msg=document.getElementById("msg");const title=document.title;const url=location.href;let txt=url;if(type=="title_url"){txt=title+"\n\n"+txt;}
navigator.clipboard.writeText(txt).then(function(){toast_msg();});}