function toast_msg(msg){msg.classList.add("toast-msg");setTimeout(function(){msg.classList.remove("toast-msg");},1500);}
function copy_url(type){let msg=document.getElementById("msg");let title=document.title;let url=location.href;if(type=="url"){var text=url;}else if(type=="title_url"){var text=title+"\n\n"+url;}else{return-1;}
navigator.clipboard.writeText(text).then(function(){toast_msg(msg);});}
