function copy_url(type){const msg=document.getElementById(`msg`);const title=document.title;const url=location.href;const txt=(()=>{if(type===`title_url`)
return title+`\n\n`+url;else
return url;})();navigator.clipboard.writeText(txt).then(()=>{msg.classList.add(`toast-msg`);setTimeout(()=>{msg.classList.remove(`toast-msg`);},1500);});}
function jump_tag(e){if(e.key!=`Enter`&&e.type!=`click`)
return;const tag_val=document.getElementById(`input-tag-comple`).value.replace(/(^\s+|\s+$)/g,``).replace(/\s+/,` `);const list=document.getElementById(`tags-list`);const selector=list.querySelector(`[value="`+tag_val+`"]`);if(selector==null){if(tag_val==``)
return;const err_msg=document.getElementById(`tag-comple-error`);err_msg.innerHTML=`"`+tag_val+`" `+`not found`;err_msg.classList.add(`toast-err`);setTimeout(()=>{err_msg.classList.remove(`toast-err`);},2000);return;}
const url="/tags/"+selector.dataset.url+"/";fetch(url,{method:`HEAD`,referrerPolicy:`no-referrer`}).then((response)=>{if(response.ok)
location.href=url;});}
function clear_value(){return document.getElementById(`input-tag-comple`).value=``;}