function copy_url(type){const msg=document.getElementById(`msg`);const title=document.title;const url=location.href;let txt=url;if(type==`title_url`){txt=title+"\n\n"+txt;}
navigator.clipboard.writeText(txt).then(()=>{msg.classList.add(`toast-msg`);setTimeout(function(){msg.classList.remove(`toast-msg`);},1500);});}
function jump_tag(e){if(e.key!=`Enter`&&e.type!=`click`)
return;const tags_val=document.getElementById(`input-tag-search`).value.replace(/(^\s+|\s+$)/g,``).replace(/\s+/,` `);const list=document.getElementById(`tags-list`);const selector=list.querySelector(`[value="`+tags_val+`"]`);if(!selector)
return;const url="/tags/"+selector.dataset.url+"/";fetch(url,{method:`HEAD`,referrerPolicy:`no-referrer`}).then((response)=>{if(response.ok)
location.href=url;});}
function clear_value(){return document.getElementById(`input-tag-search`).value=``;}